[{'sha': 'aed89cd62e8e3f2113e580ea7b90e42fe21720e4', 'filename': 'bootstrap-sortable.css', 'status': 'added', 'additions': 110, 'deletions': 0, 'changes': 110, 'blob_url': 'https://github.com/jrladd/network_navigator/blob/11583a715210d7be3c87a5b3feb5736348da8f4a/bootstrap-sortable.css', 'raw_url': 'https://github.com/jrladd/network_navigator/raw/11583a715210d7be3c87a5b3feb5736348da8f4a/bootstrap-sortable.css', 'contents_url': 'https://api.github.com/repos/jrladd/network_navigator/contents/bootstrap-sortable.css?ref=11583a715210d7be3c87a5b3feb5736348da8f4a', 'patch': '@@ -0,0 +1,110 @@
+/**
+ * adding sorting ability to HTML tables with Bootstrap styling
+ * @summary HTML tables sorting ability
+ * @version 2.0.0
+ * @requires tinysort, moment.js, jQuery
+ * @license MIT
+ * @author Matus Brlit (drvic10k)
+ * @copyright Matus Brlit (drvic10k), bootstrap-sortable contributors
+ */
+
+table.sortable span.sign {
+    display: block;
+    position: absolute;
+    top: 50%;
+    right: 5px;
+    font-size: 12px;
+    margin-top: -10px;
+    color: #bfbfc1;
+}
+
+table.sortable th:after {
+    display: block;
+    position: absolute;
+    top: 50%;
+    right: 5px;
+    font-size: 12px;
+    margin-top: -10px;
+    color: #bfbfc1;
+}
+
+table.sortable th.arrow:after {
+    content: '';
+}
+
+table.sortable span.arrow, span.reversed, th.arrow.down:after, th.reversedarrow.down:after, th.arrow.up:after, th.reversedarrow.up:after {
+    border-style: solid;
+    border-width: 5px;
+    font-size: 0;
+    border-color: #ccc transparent transparent transparent;
+    line-height: 0;
+    height: 0;
+    width: 0;
+    margin-top: -2px;
+}
+
+    table.sortable span.arrow.up, th.arrow.up:after {
+        border-color: transparent transparent #ccc transparent;
+        margin-top: -7px;
+    }
+
+table.sortable span.reversed, th.reversedarrow.down:after {
+    border-color: transparent transparent #ccc transparent;
+    margin-top: -7px;
+}
+
+    table.sortable span.reversed.up, th.reversedarrow.up:after {
+        border-color: #ccc transparent transparent transparent;
+        margin-top: -2px;
+    }
+
+table.sortable span.az:before, th.az.down:after {
+    content: "a .. z";
+}
+
+table.sortable span.az.up:before, th.az.up:after {
+    content: "z .. a";
+}
+
+table.sortable th.az.nosort:after, th.AZ.nosort:after, th._19.nosort:after, th.month.nosort:after {
+    content: "..";
+}
+
+table.sortable span.AZ:before, th.AZ.down:after {
+    content: "A .. Z";
+}
+
+table.sortable span.AZ.up:before, th.AZ.up:after {
+    content: "Z .. A";
+}
+
+table.sortable span._19:before, th._19.down:after {
+    content: "1 .. 9";
+}
+
+table.sortable span._19.up:before, th._19.up:after {
+    content: "9 .. 1";
+}
+
+table.sortable span.month:before, th.month.down:after {
+    content: "jan .. dec";
+}
+
+table.sortable span.month.up:before, th.month.up:after {
+    content: "dec .. jan";
+}
+
+table.sortable>thead th:not([data-defaultsort=disabled]) {
+    cursor: pointer;
+    position: relative;
+    top: 0;
+    left: 0;
+}
+
+table.sortable>thead th:hover:not([data-defaultsort=disabled]) {
+    background: #efefef;
+}
+
+table.sortable>thead th div.mozilla {
+    position: relative;
+}'}, {'sha': '28f7f82b6af3870abc39083fed47fb2ec439c4d6', 'filename': 'bootstrap-sortable.js', 'status': 'added', 'additions': 355, 'deletions': 0, 'changes': 355, 'blob_url': 'https://github.com/jrladd/network_navigator/blob/11583a715210d7be3c87a5b3feb5736348da8f4a/bootstrap-sortable.js', 'raw_url': 'https://github.com/jrladd/network_navigator/raw/11583a715210d7be3c87a5b3feb5736348da8f4a/bootstrap-sortable.js', 'contents_url': 'https://api.github.com/repos/jrladd/network_navigator/contents/bootstrap-sortable.js?ref=11583a715210d7be3c87a5b3feb5736348da8f4a', 'patch': '@@ -0,0 +1,355 @@
