+                })
+                .attr("y", function (label_d) {
+                    return label_d.id === d.id ? 10 : 0
+                })
+
+        })
+        .on('mouseout', function (d) {
+            circles.style('opacity', 1)
+            links
+                .style('stroke', 'grey')
+                .style('stroke-opacity', .8)
+                .style('stroke-width', '1')
