+                .style("font-size", 6)
+
+        })
+
+};'}, {'sha': 'f1729b093b6718ac243aaa19a0d90407b3f6f0cc', 'filename': 'js/forceAtlas.js', 'status': 'added', 'additions': 99, 'deletions': 0, 'changes': 99, 'blob_url': 'https://github.com/jrladd/network_navigator/blob/701512f292d4f164728b59f3ac4d908151305a4b/js%2FforceAtlas.js', 'raw_url': 'https://github.com/jrladd/network_navigator/raw/701512f292d4f164728b59f3ac4d908151305a4b/js%2FforceAtlas.js', 'contents_url': 'https://api.github.com/repos/jrladd/network_navigator/contents/js%2FforceAtlas.js?ref=701512f292d4f164728b59f3ac4d908151305a4b', 'patch': '@@ -0,0 +1,99 @@
+export function drawForceAtlas(edgeList, nodeList, colorValues) {
+    const margin = {
+        top: 75,
+        right: 200,
+        bottom: 200,
+        left: 75
+    };
+
+    var svg = d3.select('#force-atlas-viz')
+        .append("div")
+        .classed("svg-container", true)
+        .append('svg')
+        .attr("preserveAspectRatio", "xMinYMin meet")
+        .attr("viewBox", "0 0 1400 1000")
+        .classed("svg-content-responsive", true)
+        .append('g')
+        .attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');
+    
+    const width = +svg.attr('width') + 1400 - margin.left;
+    const height = +svg.attr('height') + 1000 - margin.top;
+
+    // var color = d3.scaleOrdinal(d3.schemeCategory20);
+    var color = d3.scaleOrdinal()
+        .domain(colorValues)
+        .range(["#f7fbff", "#e3eef9", "#cfe1f2", "#b5d4e9", "#93c3df", "#6daed5", "#4b97c9", "#2f7ebc", "#1864aa", "#0a4a90", "#08306b"]);
+
+    var simulation = d3.forceSimulation()
+        .force("link", d3.forceLink().id(d => d.id))
+        .force('charge', d3.forceManyBody().strength(-1000))
+        .force('collide', d3.forceCollide(18).iterations(16))
+        .force("center", d3.forceCenter(width / 2, height / 2))
+        .force('y', d3.forceY(0))
+        .force('x', d3.forceX(0));
+
+
+    var link = svg.append("g")
+        .attr("class", "links")
+        .selectAll("line")
+        .data(edgeList)
+        .enter().append("line")
+        .attr("stroke-width", d => (d.weight == 0 ? 1 : d.weight));
+
+    var node = svg.append("g")
+        .attr("class", "nodes")
+        .selectAll("g")
+        .data(nodeList)
+        .enter().append("g");
+
+    node.append("circle")
+        .attr("r", d => d.degree + 20)
+        .attr("fill", d => color(d.weight)
+        )
+        .call(d3.drag()
+            .on("start", dragstarted)
+            .on("drag", dragged)
+            .on("end", dragended));
+
+    node.append("text")
+        .text(d => d.id)
+        .attr('x', 6)
+        .attr('y', 3);
+
+    node.append("title")
+        .text(d => d.id);
+
+    simulation
+        .nodes(nodeList)
+        .on("tick", ticked);
+
+    simulation.force("link")
+        .links(edgeList);
+
+    function ticked() {
+        link.attr("x1", d =>  d.source.x)
+            .attr("y1", d => d.source.y)
+            .attr("x2", d => d.target.x)
+            .attr("y2", d => d.target.y);
+
+        node.attr("transform", d => "translate(" + d.x + "," + d.y + ")");
+    }
+
+    function dragstarted(d) {
+        if (!d3.event.active) simulation.alphaTarget(0.3).restart();
+        d.fx = d.x;
+        d.fy = d.y;
+    }
+
+    function dragged(d) {
+        d.fx = d3.event.x;
+        d.fy = d3.event.y;
+    }
+
+    function dragended(d) {
+        if (!d3.event.active) simulation.alphaTarget(0);
+        d.fx = null;
+        d.fy = null;
+    }
+
+};'}, {'sha': '7fa1e3012f559188afac7f3e85196caee9053c2e', 'filename': 'js/hist.js', 'status': 'modified', 'additions': 2, 'deletions': 2, 'changes': 4, 'blob_url': 'https://github.com/jrladd/network_navigator/blob/701512f292d4f164728b59f3ac4d908151305a4b/js%2Fhist.js', 'raw_url': 'https://github.com/jrladd/network_navigator/raw/701512f292d4f164728b59f3ac4d908151305a4b/js%2Fhist.js', 'contents_url': 'https://api.github.com/repos/jrladd/network_navigator/contents/js%2Fhist.js?ref=701512f292d4f164728b59f3ac4d908151305a4b', 'patch': '@@ -3,8 +3,8 @@ export function drawHist(data) {

