+        .force("center", d3.forceCenter(width / 2, height / 2))
+        .force('y', d3.forceY(0))
+        .force('x', d3.forceX(0));
+
+
+    var link = svg.append("g")
+        .attr("class", "links")
+        .selectAll("line")
+        .data(edgeList)
+        .enter().append("line")
+        .attr("stroke-width", function (d) {
+            return d.weight / 2
+        });
+
+    var node = svg.append("g")
+        .attr("class", "nodes")
+        .selectAll("g")
+        .data(nodeList)
+        .enter().append("g")
+
+    var circles = node.append("circle")
+        .attr("r", function (d) {
+            return d.value
+        })
+        .attr("fill", function (d) {
+            return color(d.community);
+        })
+        .call(d3.drag()
+            .on("start", dragstarted)
